export class PackageType {
  id: number
  name: string
  packages: Array<Package> 
  totalPackages: number

  constructor(id:number, name: string) {
    this.id = id
    this.name = name
  }

  setTotal(total: number) {
    this.totalPackages = total
  }

  addPackage(_package: Package) {
    this.packages.push(_package)
  }
}

export class Package {
  id: number
  idPackage: number
  announcementPaperPhotoAvailable: number
  announcementPaperPhotoEntityPackage: number
  activationDateEntityPackage: string
  endDateEntityPackage: string
  registerDateEntityPackage: string
  entityPackageName: string
  priceEntityPackage: number
  idPackageDurationPrice: number
  details: object
  
  constructor(
    id: number,
    idPackage: number,
    announcementPaperPhotoAvailable: number,
    announcementPaperPhotoEntityPackage: number,
    activationDateEntityPackage: string,
    endDateEntityPackage: string,
    registerDateEntityPackage: string,
    entityPackageName: string,
    priceEntityPackage: number,
    idPackageDurationPrice: number
  ) {
    this.id = id
    this.idPackage = idPackage
    this.announcementPaperPhotoAvailable = announcementPaperPhotoAvailable
    this.announcementPaperPhotoEntityPackage = announcementPaperPhotoEntityPackage
    this.activationDateEntityPackage = activationDateEntityPackage
    this.endDateEntityPackage = endDateEntityPackage
    this.registerDateEntityPackage = registerDateEntityPackage
    this.entityPackageName = entityPackageName
    this.priceEntityPackage = priceEntityPackage
    this.idPackageDurationPrice = idPackageDurationPrice
  }

  setDetails(details: object){
    this.details = details
  }
}

